import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Button,
  Row,
  NavLink,
  ButtonGroup,
} from "reactstrap";
import urlConfig from "../config/urlsconfig.json";
import config from "../config/config.json";
import { Link, useHistory } from "react-router-dom";
const _ = require("lodash");

const BlogCard = (props) => {
  const history = useHistory();
  const userId = localStorage.getItem("userId");
  const requetsedUserId = props.userId;
  let [isLiked, setIsLiked] = useState(
    props.likes.includes(userId) ? true : false
  );
  let [_likes, setLikes] = useState(props.likes);

  const handleLike = (blogId, likes) => {
    if (!isLiked) {
      var temp = _.clone(_likes);
      temp.push(userId);
      setLikes((_likes = temp));
    } else {
      var temp = _.clone(_likes);
      _.remove(temp, (id) => {
        return id === userId;
      });
      setLikes((_likes = temp));
    }
    setIsLiked((isLiked = !isLiked));
    let body = JSON.stringify({
      likes: _likes,
    });
    const token = localStorage.getItem("token");

    let url = `${config.baseURL}${urlConfig.updateblog.uri}`;
    url = url.replace(":id", props._id);
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.updateblog.method,
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
      body: body,
    }).then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    });
  };
  return (
    <div>
      <React.Fragment>
        <Card>
          <CardBody>
            <CardText>
              {props.userName ? (
                <React.Fragment>
                  <p>
                    Posted by{" "}
                    <Link
                      onClick={() => history.push(`/userblog/${props.userId}`)}
                    >
                      {props.userName}
                    </Link>
                  </p>
                <p></p>
                </React.Fragment>
              ) : (
                <div />
              )}
            </CardText>
            <CardImg width="400px" height="500px" src={props.media} />
            <CardTitle>
              <h5 className="display-7">{props.title}</h5>
            </CardTitle>
            <CardSubtitle>
              <h6 className="display-8">{props.subtitle}</h6>
            </CardSubtitle>
            <hr />
            <CardText>
              <p className="lead-small">{props.description}</p>
            </CardText>
            <hr />
            <p className="lead-small"> Liked By {_likes.length}</p>
            <Button
              color="info"
              size="sm"
              onClick={() => handleLike(props._id, props.likes)}
            >
              {isLiked ? "Liked" : "Like"}
            </Button>
            <p> </p>
            {userId === requetsedUserId ? (
              <ButtonGroup>
                <Button size="sm" color="secondary">
                  Edit
                </Button>
                <Button size="sm" color="danger">
                  Delete
                </Button>
              </ButtonGroup>
            ) : (
              <React.Fragment />
            )}
          </CardBody>
        </Card>
      </React.Fragment>
    </div>
  );
};

export default BlogCard;
