import React, {  useContext, useState } from 'react';
import LoginContext from '../context/loginContext';
import { 
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button,
    Badge,
} from 'reactstrap';
import { Link, Redirect, useHistory } from 'react-router-dom';
const moment = require("moment");

const Header = (props) => {
    const loginContext = useContext(LoginContext);
    const { isLoggedIn, toggleLogIn } = useContext(LoginContext);
    let [time, updateTime] = useState(moment().format('MMMM Do YYYY, h:mm:ss a'));

    let history = useHistory();
    const handleLogout = () => {
        localStorage.removeItem("token");
        toggleLogIn();
        history.push('/login');
    };

    setInterval(() => {
        updateTime(moment().format('MMMM Do YYYY, h:mm:ss a'))
    }, 1000);

    return (
        <div>
            <Navbar color = "info" expand = "lg" fixed = "top">
            <NavbarBrand className="navbar-brand"href="/"><h4>Content Management System</h4></NavbarBrand>
            <Nav className = "mr-auto" navbar>
            <NavItem>
            {loginContext.isLoggedIn?
                <Button color="white" onClick = {handleLogout}>LOGOUT</Button>
                :
                <Link to = '/login'>
                <Button color="white">LOGIN</Button>
                </Link>
            }
                    
            </NavItem>
            </Nav>
            </Navbar>
            </div>
    )
}

export default Header;
