import React, { useEffect, useState } from "react";

import config from "../config/config.json";
import urlConfig from "../config/urlsconfig.json";
import BlogCard from "./BlogCard";

const UserBlog = () => {
  let [userBlog, setUserBlog] = useState([]);

  const objToQueryString = (obj) => {
    const keyValuePairs = [];
    for (const key in obj) {
      keyValuePairs.push(
        encodeURIComponent(key) + "=" + encodeURIComponent(obj[key])
      );
    }
    return keyValuePairs.join("&");
  };

  const fetchUserBlog = () => {
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");
    let url = `${config.baseURL}${urlConfig.getblogs.uri}`;
    url = url.replace(":userId", userId);
    let queryParams = {
      userId: userId,
    };
    url = `${url}?${objToQueryString(queryParams)}`;
    fetch(url, {
      method: urlConfig.getblogs.method,
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        if (response) {
          setUserBlog(response);
        }
      });
  };

  useEffect(() => {
    fetchUserBlog();
  }, []);

  return (
    <div>
      <br />
      <br />
      <BlogCard/>

      <br />
      <h1>UserBlog</h1>
    </div>
  );
};

export default UserBlog;
