import React from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Button,
  Row,
} from "reactstrap";
const _ = require("lodash");

const BlogCard = (props) => {
  const userId = localStorage.getItem("userId");
  let [isLiked, setIsLiked] = useState(likes.includes(userId) ? true : false);
  let [_likes, setLikes] = useState(props.likes);

  const handleLike = (blogId, likes) => {
    if (isLiked) {
      _likes.push(userId);
    } else {
      _.remove(likes, userId);
    }
  };
  return (
    <div>
      <React.Fragment>
        <Card>
          <CardBody>
            <CardText>{props.userName}</CardText>
            <CardImg width="400px" height="500px" src={props.media} />
            <CardTitle>
              <h5 className="display-7">{props.title}</h5>
            </CardTitle>
            <CardSubtitle>
              <h6 className="display-8">{props.subtitle}</h6>
            </CardSubtitle>
            <hr />
            <CardText>
              <p className="lead-small">{props.description}</p>
            </CardText>
            <hr />
            <p className="lead-small"> Liked By {props.likes.length}</p>
            <Button
              color="info"
              size="sm"
              onClick={() => handleLike(props._id, props.likes)}
            >
              Like
            </Button>
          </CardBody>
        </Card>
      </React.Fragment>
    </div>
  );
};

export default BlogCard;
