import React, { useEffect, useState } from "react";
import {
  Jumbotron,
  Button,
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardText,
} from "reactstrap";
import { useHistory } from "react-router-dom";
import BlogCard from "../Templates/BlogCard";
import AddBlog from "../Templates/addBlog";

const Home = () => {
  let [blogs, setBlogs] = useState("");
  const history = useHistory();
  const obj = {
    title: "Avengers",
    subtitle: "EndGame",
    description: "Love You 3000",
  };
  const fetchBlog = () => {
    const token = getToken();
    let url = `${config.baseURL}${urlConfig.getblogs.uri}`;
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.getblogs.method,
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        if (response) {
          setBlogs(response.blogs);
        }
      });
  };
  useEffect(() => {
    fetchBlog(match.params.userId);
  }, []);
  return (
    <React.Fragment>
      <br />
      <br />
      <br />
      <Row>
        <Col xs="1"></Col>
        <Col xs="6">
          {blogs.map((blog) => {
            return (
              <Row>
                <BlogCard
                  title="Avengers"
                  subtitle="EndGame"
                  description="Marvel's The Avengers[6] (classified under the name Marvel Avengers Assemble in the United Kingdom and Ireland[3][7]), or simply The Avengers, is a 2012 American superhero film based on the Marvel Comics superhero team of the same name. Produced by Marvel Studios and distributed by Walt Disney Studios Motion Pictures,[N 1] it is the sixth film in the Marvel Cinematic Universe (MCU). Written and directed by Joss Whedon, it features an ensemble cast including Robert Downey Jr., Chris Evans, Mark Ruffalo, Chris Hemsworth, Scarlett Johansson, and Jeremy Renner as the Avengers, alongside Tom Hiddleston, Clark Gregg, Cobie Smulders, Stellan Skarsgård, and Samuel L. Jackson. In the film, Nick Fury and the spy agency S.H.I.E.L.D. recruit Tony Stark, Steve Rogers, Bruce Banner, and Thor to form a team capable of stopping Thor's brother Loki from subjugating Earth."
                  media="https://picsum.photos/200/300"
                  userName="Sunny"
                />
              </Row>
            );
          })}
        </Col>
        <Col xs="4">
          <Card>
            <CardBody>
              <CardText>Click Below to Add a New Blog</CardText>
              <Button
                color="info"
                onClick={() => {
                  history.push("/addblog");
                }}
              >
                New Blog
              </Button>
            </CardBody>
          </Card>
        </Col>
        <Col xs="1"></Col>
      </Row>
    </React.Fragment>
  );
};

export default Home;
